mapper.select:
select

from ttc_favorite


    and favorite_owner = #{favoriteOwner}


    and favorite_name = #{favoriteName}

mapper.update:
update ttc_favorite
set favorite_name = #{favoriteName,jdbcType=VARCHAR},
  favorite_owner = #{favoriteOwner,jdbcType=VARCHAR},
  favorite_createtime = #{favoriteCreatetime,jdbcType=TIMESTAMP}
where favorite_id = #{favoriteId,jdbcType=VARCHAR}
